sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	g_form.getReference('consumer', function(consumer) {
		var firstName = consumer.getValue('first_name');
		var middleName = consumer.getValue('middle_name');
		var lastName = consumer.getValue('last_name');
		var birthDate = consumer.getDisplayValue('u_birth_date');

		g_form.setValue('first_name', firstName);
		g_form.setValue('u_middle_name', middleName);
		g_form.setValue('last_name', lastName);
		g_form.setValue('u_birth_date', birthDate);
	});

}
